import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { Marker as LeafletMarker} from 'react-leaflet';
import L from 'leaflet';

// This is supposed to be the fix for webpack, and works for create-react-app,
// but does not work in this case
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});


// WARNING, HARDCODED IMAGES! THIS IS A VERY TEMPORARY FIX, BECAUSE THE WEBPACK
// FIX IS NOT SUPPORTED BY DASH! THIS IS AN EXTREMELY COMPLICATED TASK
const iconUrl = "data:image/png;base64,";

const iconRetinaUrl = "data:image/png;base64,"

const shadowUrl = "data:image/png;base64,"


/**
 * Description to be added
 */
export default class Marker extends Component {
  constructor(props) {
    super(props);
  }

  render() {

    const {
      id,
      setProps,
      children,
      position,
      draggable,
      opacity,
      iconAnchor,
      iconSize,
      popupAnchor,
      shadowSize,
      tooltipAnchor
    } = this.props;

    // // Uncomment below to investigate the problem with L.Icon.Default
    // console.log(L.Icon.Default.prototype.options);
    // console.log("marker2x:", require('leaflet/dist/images/marker-icon-2x.png'));

    return (
      <LeafletMarker
        position={position}
        opacity={opacity}
        draggable={draggable}
        icon={L.icon({
          iconUrl: iconUrl,
          iconRetinaUrl: iconRetinaUrl,
          shadowUrl: shadowUrl,
          iconAnchor: iconAnchor,
          iconSize: iconSize,
          popupAnchor: popupAnchor,
          shadowSize: shadowSize,
          tooltipAnchor: tooltipAnchor
        })}
      >
        {children}
      </LeafletMarker>
    );
  }
};

Marker.defaultProps = {
  iconAnchor: [12, 41],
  iconSize: [25, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
  tooltipAnchor: [16, -28]
}

Marker.propTypes = {

  /**
   * The ID used to identify this component in Dash callbacks
   */
  id: PropTypes.string,

  /**
   * ...
   */
  children: PropTypes.node,

  /**
   * To be added
   */
  position: PropTypes.object,

  /**
   * To be added
   */
  draggable: PropTypes.bool,

  /**
   * To be added
   */
  zIndexOffset: PropTypes.number,

  /**
   * To be added
   */
  opacity: PropTypes.number,


  /**
   * https://leafletjs.com/reference-1.3.4.html#icon
   */
  iconAnchor: PropTypes.array,

  /**
   * https://leafletjs.com/reference-1.3.4.html#icon
   */
  iconSize: PropTypes.array,

  /**
   * https://leafletjs.com/reference-1.3.4.html#icon
   */
  popupAnchor: PropTypes.array,

  /**
   * https://leafletjs.com/reference-1.3.4.html#icon
   */
  shadowSize: PropTypes.array,

  /**
   * https://leafletjs.com/reference-1.3.4.html#icon
   */
  tooltipAnchor: PropTypes.array,

  /**
   * Dash-assigned callback that should be called whenever any of the
   * properties change
   */
  setProps: PropTypes.func
}
